import React , {useState} from 'react';
import Routing from "../Routing";
import { Map, Marker, Popup, TileLayer } from "react-leaflet";
import L from 'leaflet';
import iconGreen from '../../assets/GreenMarker.png';
export default function MapView(){


    const dataPoints = [
        [49.24167849096564, 4.061995752829034],
        [ 49.467134581917357,4.546518086577947],
        [ 49.295014379864874,4.898610599532319],

    ]

    var greenIcon = L.icon({
        iconUrl: iconGreen,
        iconSize:     [31, 47], // size of the icon
        iconAnchor:   [15, 47], // point of the icon which will correspond to marker's location
        popupAnchor:  [-3, -76] // point from which the popup should open relative to the iconAnchor
    });
    const [wayPoints , setWayPoints] = useState([dataPoints[0] , dataPoints[4]])
    let [refMap , setRefMap] = useState(null);
    const saveMap = (map)=>{
        setRefMap(map)
    }
    return(


        <div className="mapHolder">
            <input label = "Adresse"type = "text"/>


            <button onClick ={()=>{
                setWayPoints([
                    dataPoints[1],
                    dataPoints[2],
                ])
            }}>
                Change
            </button>
            <Map center={[49.24167849096564, 4.061995752829034]} zoom={11} ref={saveMap}>
                <TileLayer
                    url='https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png'
                    attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                />
                { dataPoints.map((data , index)=>{
                    return(
                        <Marker icon={greenIcon} key={index} position={data}>
                            <Popup>
                                Sample Popup
                            </Popup>
                        </Marker>
                    )
                })}

                <Routing
                    map={refMap}
                    show={true}
                    waypoints={wayPoints}
                    lineStyles={{
                        color: 'red',
                        opacity: 1,
                        weight: 5
                    }}
                />
            </Map>
        </div>
    )
}
