import React , {useState} from 'react';
import Routing from "../Routing";
import { Map, Marker, Popup, TileLayer } from "react-leaflet";
export default function MapView(){

    const dataPoints = [
        [49.24167849096564, 4.061995752829034],
        [ 49.467134581917357,4.546518086577947],
        [ 49.295014379864874,4.898610599532319],

    ]
    const [wayPoints , setWayPoints] = useState([dataPoints[0] , dataPoints[4]])
    let [refMap , setRefMap] = useState(null);
    const saveMap = (map)=>{
        setRefMap(map)
    }
    return(
        <div className="mapHolder">
            <button onClick ={()=>{
                setWayPoints([
                    dataPoints[Math.floor(Math.random()*5)],
                    dataPoints[Math.floor(Math.random()*5)],
                ])
            }}>
                Change
            </button>
            <Map center={[45.4, -75.7]} zoom={11} ref={saveMap}>
                <TileLayer
                    url='https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png'
                    attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                />
                { dataPoints.map((data , index)=>{
                    return(
                        <Marker key={index} position={data}>
                            <Popup>
                                Sample Popup
                            </Popup>
                        </Marker>
                    )
                })}

                <Routing
                    map={refMap}
                    show={true}
                    waypoints={wayPoints}
                    lineStyles={{
                        color: 'red',
                        opacity: 1,
                        weight: 5
                    }}
                />
            </Map>
        </div>
    )
}
